interface IConfig {
    as_wide: string;
}

class Progress {
    config!: IConfig;
    timer?: number;
    animateTimer?: number;
    prefix?: string;
    content?: string;
    publicStyle?: string;
    wrapBG?: string;
    times!: number;
    per!: number;
    bofqi?: Element | null;

    init(config: IConfig) {
        window.requestAnimationFrame = (function () {
            return (
                window.requestAnimationFrame ||
                window.webkitRequestAnimationFrame ||
                window.mozRequestAnimationFrame ||
                window.oRequestAnimationFrame ||
                window.msRequestAnimationFrame ||
                function (callback: Function) {
                    window.setTimeout(callback, 1000 / 60);
                }
            );
        })();
        window.cancelNextRequestAnimationFrame = (function () {
            return (
                window.cancelAnimationFrame ||
                window.webkitCancelAnimationFrame ||
                window.webkitCancelRequestAnimationFrame ||
                window.mozCancelRequestAnimationFrame ||
                window.oCancelRequestAnimationFrame ||
                window.msCancelRequestAnimationFrame ||
                clearTimeout
            );
        })();
        if (this.timer) {
            window.cancelNextRequestAnimationFrame(this.timer);
        }
        if (this.animateTimer) {
            window.cancelNextRequestAnimationFrame(this.animateTimer);
        }
        this.config = config;
        this.prefix = 'bilibili-player-video';
        this.content = this._content();
        this.publicStyle = this._publicStyle();
        this.wrapBG = this._setWrapBG();
        this.times = 1;
        this.per = 0;
        this.timer = 0;
        this.animateTimer = 0;
        this.prefix = 'bilibili-player-video';
        const playerLoadProgress = document.querySelector('.playerLoadProgress');
        if (playerLoadProgress && playerLoadProgress.parentNode) {
            playerLoadProgress.parentNode.removeChild(playerLoadProgress);
        }
        this.bofqi = document.querySelector('#bilibili-player') || document.querySelector('#bofqi');
        if (this.bofqi) {
            this.setProgressPos({
                w: this.bofqi.clientWidth,
                h: this.bofqi.clientHeight,
            });
        }
        const head = document.querySelector('head');
        if (head) {
            head.insertAdjacentHTML('beforeend', `<style class="playerLoadProgress">${this.publicStyle}</style>`);
            if (!this._isPGC()) {
                head.insertAdjacentHTML('beforeend', `<style class="playerLoadProgress">${this.wrapBG}</style>`);
            }
        }
        if (this.bofqi) {
            this.bofqi.insertAdjacentHTML('beforeend', this.content);
        }
        this._bind();
        this.start();
    }
    setProgressPos(o: { w: number; h: number }) {
        const containerWidth = o.w;
        const containerHeight = o.h;
        try {
            if (
                this.config.as_wide === '1' ||
                (JSON.parse(localStorage.bilibili_player_settings).video_status.widescreensave &&
                    JSON.parse(localStorage.bilibili_player_settings).video_status.iswidescreen)
            ) {
                // 宽
                this._setContainerWH({
                    width: containerWidth,
                    height: containerHeight - 68,
                });
            } else {
                // 正常
                this._setContainerWH({
                    width: containerWidth - 300,
                    height: containerHeight - 116,
                    top: 50,
                });
            }
        } catch (e) {
            // 正常
            this._setContainerWH({
                width: containerWidth - 300,
                height: containerHeight - 116,
                top: 50,
            });
        }
    }
    _bind() {
        window.addEventListener('scroll', () => {
            const loadWrap = document.querySelector(`.${this.prefix}-load-wrap`);
            const maskBg: HTMLElement | null = document.querySelector(`.${this.prefix}-mask-bg`);
            if (this._isMiniScreen() && !(loadWrap && loadWrap.classList.contains(`${this.prefix}-mini-mode`))) {
                loadWrap && loadWrap.classList.add(`${this.prefix}-mini-mode`);
                maskBg && (maskBg.style.display = 'none');
            } else {
                loadWrap && loadWrap.classList.remove(`${this.prefix}-mini-mode`);
                maskBg && (maskBg.style.display = 'block');
            }
        });
        window.addEventListener('resize', () => {
            const loadWrap = document.querySelector(`.${this.prefix}-load-wrap`);
            if (loadWrap && this.bofqi) {
                this.setProgressPos({
                    w: this.bofqi.clientWidth,
                    h: this.bofqi.clientHeight,
                });
            }
        });
    }
    _content() {
        return `
            <div class="${this.prefix}-mask-bg"></div>
            <div class="${this.prefix}-load-wrap">
                <div class="${this.prefix}-load-content">
                    <div class="${this.prefix}-load-tv">
                        <div class="${this.prefix}-load-mask"></div>
                        <img class="small-tv-ico" src="data:image/gif;base64,">
                    </div>
                    <span class="${this.prefix}-load-progress-title">Loading(
                        <span class="${this.prefix}-load-percent">0%</span>)...
                    </span>
                </div>
            </div>
        `;
    }
    _publicStyle() {
        return `
            .${this.prefix}-mask-bg {
                width: 100%;
                height: 100%;
                position: absolute;
                top: 0;
                left: 0;
                background-color: #fff;
            }

            .${this.prefix}-load-wrap {
                height: 100%;
                position: absolute;
                top: 0;
                left: 0;
                z-index: 9999
            }

            .${this.prefix}-load-content {
                position: absolute;
                top: 50%;
                left: 50%;
                transform: translate(-50%, -50%);
                margin: auto;
                width: 178px;
                height: 90px;
            }

            .${this.prefix}-load-tv {
                margin-top: 55px;
                width: 90px;
                height: 90px;
                overflow: hidden;
                position: relative;
                margin: 0 auto;
            }

            .${this.prefix}-load-mask {
                width: 90px;
                height: 90px;
                background: rgba(255, 255, 255, .4);
                position: absolute;
                top: 0;
                left: 0;
            }

            .${this.prefix}-load-progress-title {
                white-space: nowrap;
                font-family: "Times New Roman";
                // display: inline-block;
                height: 34px;
                line-height: 34px;
                font-size: 28px;
            }
            .${this.prefix}-mini-mode {
                width: 100% !important;
                height: 100% !important;
                top: 0 !important;
            }
        `;
    }
    _setWrapBG() {
        return `
            .${this.prefix}-mask-bg {
                width: 100%;
                height: 100%;
                position: absolute;
                top: 0;
                left: 0;
                background: url('data:image/png;base64,');
                background-size: 100% 100%;
            }
            @media screen and (min-width: 1400px) {
                .${this.prefix}-mask-bg {
                    width: 100%;
                    height: 100%;
                    position: absolute;
                    top: 0;
                    left: 0;
                    background: url('data:image/png;base64,');
                    background-size: 100% 100%;
                }
            }
        `;
    }
    _isMiniScreen() {
        return document.querySelector('.newfloat') || document.querySelector('.mini-player');
    }
    _isPGC() {
        return Boolean(window.location.href.match('www.bilibili.com/bangumi'));
    }
    _setContainerWH(o: { width: number; height: number; top?: number }) {
        const loadWrap: HTMLElement | null = document.querySelector(`.${this.prefix}-load-wrap`);
        if (loadWrap) {
            loadWrap.style.width = `${o.width}px`;
            loadWrap.style.height = `${o.height}px`;
            loadWrap.style.top = `${o.top ? o.top : 0}px`;
        } else {
            const style = `
            .${this.prefix}-load-wrap {
                width: ${o.width}px;
                height: ${o.height}px;
                top: ${o.top ? o.top : 0}px;
            }
            `;
            const head = document.querySelector('head');
            head && head.insertAdjacentHTML('beforeend', `<style>${style}</style>`);
        }
    }
    start() {
        const that = this,
            loading = () => {
                if (that.per <= 80) {
                    that.per = Math.floor((Math.log(that.times) / Math.log(15.6)) * 100);
                } else {
                    that.per += 60 / 2000;
                }
                const mask: HTMLElement | null = document.querySelector(`.${that.prefix}-load-mask`);
                if (mask) {
                    mask.style.height = `${100 - Math.floor(that.per)}%`;
                }
                const lodPercent = document.querySelector(`.${this.prefix}-load-percent`);
                if (lodPercent) {
                    lodPercent.innerHTML = `${Math.floor(that.per)}%`;
                }
                // MAX value: 99%
                // tip: the rate of logarithmic function
                that.times += 1;
                if (Math.floor(that.per) < 99) {
                    that.timer = window.requestAnimationFrame(loading);
                }
            };
        window.requestAnimationFrame(loading);
    }
    end() {
        const that = this,
            totalStep = Math.ceil(100 - that.per);
        const maskBg = document.querySelector(`.${this.prefix}-mask-bg`);
        if (maskBg && maskBg.parentNode) {
            maskBg.parentNode.removeChild(maskBg);
        }

        let step = 1;
        if (totalStep / 60 > 0.3) {
            step = Math.ceil(totalStep / 20);
        }
        function ending() {
            const smallTvIco: HTMLElement | null = document.querySelector('.small-tv-ico');
            smallTvIco && (smallTvIco.style.display = 'none');
            const loadWrap = document.querySelector(`.${that.prefix}-load-wrap`);
            if (that._isMiniScreen()) {
                if (loadWrap && loadWrap.parentNode) {
                    loadWrap.parentNode.removeChild(loadWrap);
                }
            }
            that.per += step;
            if (that.per >= 100) {
                that.per = 100;
            }
            const mask: HTMLElement | null = document.querySelector(`.${that.prefix}-load-mask`);
            if (mask) {
                mask.style.height = `${100 - Math.floor(that.per)}%`;
            }
            const loadPercent = document.querySelector(`.${that.prefix}-load-percent`);
            if (loadPercent) {
                loadPercent.innerHTML = `${Math.floor(that.per)}%`;
            }
            if (Math.floor(that.per) < 100) {
                that.animateTimer = window.requestAnimationFrame(ending);
            } else {
                if (loadPercent) {
                    loadPercent.innerHTML = `${100}%`;
                }
                if (mask) {
                    mask.style.height = '0%';
                }
                setTimeout(() => {
                    // 手动绘制100%
                    if (loadWrap && loadWrap.parentNode) {
                        loadWrap.parentNode.removeChild(loadWrap);
                    }
                }, 1000 / 60);
            }
        }
        window.requestAnimationFrame(ending);
    }
}

const loadProgress = new Progress();

export default loadProgress;

//////////////////////////// 全局增强 ////////////////////////////
declare global {
    interface Window {
        webkitRequestAnimationFrame?: typeof requestAnimationFrame;
        mozRequestAnimationFrame?: typeof requestAnimationFrame;
        oRequestAnimationFrame?: typeof requestAnimationFrame;
        msRequestAnimationFrame?: typeof requestAnimationFrame;

        cancelNextRequestAnimationFrame: typeof cancelAnimationFrame;
        webkitCancelAnimationFrame?: typeof cancelAnimationFrame;
        webkitCancelRequestAnimationFrame?: typeof cancelAnimationFrame;
        mozCancelRequestAnimationFrame?: typeof cancelAnimationFrame;
        oCancelRequestAnimationFrame?: typeof cancelAnimationFrame;
        msCancelRequestAnimationFrame?: typeof cancelAnimationFrame;
    }
}